import {signInWithPopup } from "firebase/auth";
import {auth, provider} from '../firebase/firebase'
import { useState } from "react";

export const SocialMediaSignIn = () => {
    signInWithPopup(auth, provider)
    .then((result) => {
      let user = result.user;
      console.log(user)

    }).catch((error) => {
      console.log(error)
    })
  }
