      myImageTag.style.width = '100px'
      myImageTag.style.height = '100px'
      myImageTag.style.padding = '10px'
    }
  }
  return (
    <div className="page" style={{ textAlign: "center", paddingTop: "20px" }}>
      {user ? (
        <CreatePost />
      ) : (
        <div className="sign-in">
          <Signinbtn />
          <p style={{ marginLeft: "10px" }}>To Post & Comment</p>
        </div>
      )}
    </div>
  );
};

export default Page;
