import React, {useContext, useEffect} from 'react'
import {UserContext} from '../Context/userContext'
import {db} from '../firebase/firebase'
import { doc, getDoc } from "firebase/firestore";
import './AllPosts.css'


const AllPosts = () => {
    const [user, setUser] = useContext(UserContext).user;
    const post = [
      {username:'Amer Shboul', profileURL: "https://picsum.photos/200/300", imageURL: "https://picsum.photos/200/300", caption: "lorem ipsasm lorem ipsasm lorem ipsasm lorem ipsasm lorem ipsasm lorem ipsasm ", comments:{0: 'hahahah', 1: "LOL", 2:"FUNNY"} }
    ]

    useEffect(async () => {
      const docRef = doc(db, "posts", "yzVFy1Qm8Ll8q8dtK2FS");
      const docSnap = await getDoc(docRef);
      if (docSnap.exists()) {
        console.log("Document data:", docSnap.data());
      } else {
        // doc.data() will be undefined in this case
        console.log("No such document!");
      }

    }, [])
    
    return (
        <>
        <div className='posts'>
          <div className="post">
            <div className="post-header">
              <img src={post[0].profileURL} alt="" />
              <h3 className='username'>{post[0].username}</h3>
              <button className='delete-post'>Delete</button>
            </div>
            <div className="post-image">
              <img src="https://wallpapercave.com/wp/wp4939880.png" alt="" />
            </div>
            <div className="post-caption">
            <span>{post[0].username}</span> 
            <p>{post[0].caption}</p>
            </div>
            <div className="post-comments">
            <p className='username-comment'>{post[0].username}</p>
            <p className='comment'>{post[0].comments[0]}</p>
            </div>
          </div>
        </div>
        </>
    )
}

export default AllPosts
