import React, {useContext, useEffect, useState} from 'react'
import {UserContext} from '../Context/userContext'
import {db} from '../firebase/firebase'
import { doc, getDoc, query, getDocs, collection, deleteDoc, setDoc, updateDoc  } from "firebase/firestore";
import './AllPosts.css'


const AllPosts = () => {
    const [user, setUser] = useContext(UserContext).user;
    const [docs, setDocs] = useState([]);
    const [comment, setComment] = useState(null)
    const q = query(collection(db, "posts"));

    const handleDeletePost = async (id) => {
      await deleteDoc(doc(db, "posts", id));
    }

    const handleAddComment = async (id) => {
      await updateDoc(doc(db, "posts", id), {
        comments: {
          username: user.displayName,
          comment: 'zzz',
        }
      });
      setComment('')
    }


    useEffect(async () => {
      const querySnapshot = await getDocs(q);
      setDocs(querySnapshot.docs.map(doc => ({id: doc.id, post: doc.data()})))
    }, [q])
    
    return (  

      <div className="posts">
        {docs.length > 0 ? (
          <>
          { docs.map( ({id, post}) => (
          <div className="post" key={id}>
            <div className="post-header">
              <img src={post.profileUrl} alt="profileUrl" />
              <h3 className='username'>{post.username}</h3>
              <button className='delete-post' onClick={() => handleDeletePost(id)}>Delete</button>
            </div>
            <div className="post-image">
              <img src={post.photoUrl} alt={post.username} />
            </div>
            <div className="post-caption">
              <span>{post.username}</span>
              <p>{post.post}</p>
            </div>
            <div className="post-comments">
              <p className='username-comment'>aa</p>
              <p className='comment'>hahaha</p>
            </div>
            {user && <div className="add-comment">
              <textarea rows='1' placeholder='Add a comment' value={comment ? comment : ""} onChange={e => setComment(e.target.value)}></textarea>
              <button className='add-comment-btn' onClick={() => handleAddComment(id)}>Add</button>
            </div>}
          </div>
        ) ) }
          </>
        ) : <div className="posts"> <p>No posts yet , Start adding some 📝 ...</p> </div> }
      </div>
    )}

export default AllPosts
