import { UserContext } from "../Context/userContext";
import { db, storage, imagesRef } from "../firebase/firebase";
import { getStorage, ref, uploadBytesResumable, getDownloadURL } from "firebase/storage";


import "./CreatePost.css"
const CreatePost = () => {


  const [user, setUser] = useContext(UserContext).user;
  const [post, setPost] = useState("");
  const [image, setImage] = useState(null);
  const [progress, setProgress] = useState(null);


  const generateId = (length) => {
    let allCarc = "ABCDEFGHIJKLMNOPQRSTUVWXYZ1234567890";
    let myId = '';
    for (var i = 0; i < length ; i++){
        myId += allCarc[Math.floor(Math.random() * allCarc.length)] 
    }
    return myId
  }


  const handleUpload = () => {
    const imagesRef = ref(storage, 'images');
    const uploadTask = uploadBytesResumable(imagesRef, file)
    uploadTask.on('state_changed',(snapshop) => {
      const progress = (snapshot.bytesTransferred / snapshot.totalBytes) * 100;
      console.log('Upload is ' + progress + '% done');
    },(error) => {
      console.log(error)
    },() => {
      getDownloadURL(imagesRef).then(imageURL => {
        console.log(imageURL)
      })
    })
  }


  const handleImage = (e) => {
    if(e.target.files[0]){

      setImage(e.target.files[0])

      var urlObject = URL.createObjectURL(e.target.files[0])

      var myImageTag = document.getElementById('Image-Preview')
      myImageTag.src = urlObject
      myImageTag.style.display = 'block'
      myImageTag.style.width = '100px'
      myImageTag.style.height = '100px'
      myImageTag.style.padding = '10px'
    }
  }


    return (
        <div className="create-post">
            <p>Create a Post</p>
            <textarea
            placeholder='Enter a post...'
            value={post}
            onChange={e => setPost(e.target.value)}>
            </textarea>
            <div className="image">
              <img alt="img" id='Image-Preview' />
            </div>
            <div className="actions">
            <label htmlFor="addImage" style={{cursor:"pointer"}}><i className="fas fa-camera"></i></label>
            <input type="file" id='addImage' accept='image/*' style={{display: "none"}} onChange={handleImage}/>
            <button onClick={handleUpload} style={{color: post || image ? 'black' : '#999'}} disabled={!post}>Upload</button>
            </div>
        </div>
    )
}

export default CreatePost
