import React, { useContext, useState } from "react";
import Signinbtn from "../SignIn-Btn/Signinbtn";
import { UserContext } from "../Context/userContext";
import "./Page.css";
const Page = () => {
  const [user, setUser] = useContext(UserContext).user;
  const [post, setPost] = useState("");
  const [image, setImage] = useState(null);
  const handleUpload = () => {

  }
  const handleImage = (e) => {
    if(e.target.files[0]){
      setImage(e.target.files[0])
      var urlObject = URL.createObjectURL(image)
      console.log(urlObject)
      var myImageTag = document.getElementById('Image-Preview')
    }
  }
  return (
    <div className="page" style={{ textAlign: "center", paddingTop: "20px" }}>
      {user ? (
        <div className="create-post">
            <p>Create a Post</p>
            <textarea
            placeholder='Enter a post...'
            value={post}
            onChange={e => setPost(e.target.value)}>
            </textarea>
            <div className="image">
              <img src={image} alt="img" id='Image-Preview' />
            </div>
            <div className="actions">
            <label htmlFor="addImage" style={{cursor:"pointer"}}><i class="fas fa-camera"></i></label>
            <input type="file" id='addImage' accept='image/*' style={{display: "none"}} onChange={handleImage}/>
            <button onClick={handleUpload} style={{color: post ? 'black' : '#999'}} disabled={!post}>Upload</button>
            </div>
        </div>
      ) : (
        <div className="sign-in">
          <Signinbtn />
          <p style={{ marginLeft: "10px" }}>To Post & Comment</p>
        </div>
      )}
    </div>
  );
};

export default Page;
