import firebase from "../firebase/firebase";
export const googleProvider = firebase.auth.GoogleAuthProvider(); 

export const socialMediaProvider = (provider) => {
  return firebase
  .auth()
  .signInWithPopup(provider)
  .then(res => {
    return res.user;
  })
  .catch(err => {
    return err
  })
}
